// src/components/CandidateList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';

function CandidateList() {
  const [candidates, setCandidates] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchCandidates = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:8000/candidats/list_candidates.php', {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        setCandidates(response.data);
      } catch (error) {
        console.error(error);
        setMessage('Erreur lors de la récupération de la liste des candidats.');
      }
    };
    fetchCandidates();
  }, []);

  return (
    <div>
      <Navbar />
      <div className="container">
      <h2>Liste des Candidats</h2>
      {message && <p>{message}</p>}
      <ul>
        {candidates.map(candidate => (
          <li key={candidate.id}>
            <Link to={`/candidates/${candidate.id}`}>
              {candidate.nom} {candidate.prenom} - {candidate.parti_politique}
            </Link>
          </li>
        ))}
      </ul>
      </div>
    </div>
  );
}

export default CandidateList;
