// src/components/UploadElectors.js
import React, { useState } from 'react';
import Navbar from './Navbar';
import axios from 'axios';

function UploadElectors() {
  const [file, setFile] = useState(null);
  const [checksum, setChecksum] = useState('');
  const [message, setMessage] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file || !checksum) {
      setMessage('Please select a file and enter the checksum.');
      return;
    }
    const formData = new FormData();
    formData.append('file', file);
    formData.append('checksum', checksum);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:8000/api/electeurs/upload_list.php', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`,
        },
      });
      setMessage(response.data.message);
    } catch (err) {
      setMessage('Upload failed.');
    }
  };

  return (
    <div>
      <Navbar />
      <h2>Upload Electors CSV</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <input type="file" onChange={handleFileChange} accept=".csv" required />
        </div>
        <div>
          <label>Checksum: </label>
          <input 
            type="text" 
            value={checksum} 
            onChange={(e) => setChecksum(e.target.value)} 
            required 
          />
        </div>
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default UploadElectors;
