// src/components/SetPeriod.js
import React, { useState } from 'react';
import Navbar from './Navbar';
import axios from 'axios';

function SetPeriod() {
  const [dateDebut, setDateDebut] = useState('');
  const [dateFin, setDateFin] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost/api/parrainages/set_parrainage_period.php', {
        date_debut: dateDebut,
        date_fin: dateFin,
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });
      setMessage(response.data.message);
    } catch (err) {
      setMessage('Error setting period.');
    }
  };

  return (
    <div>
      <Navbar />
      <h2>Set Parrainage Period</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Date DÃ©but: </label>
          <input type="date" value={dateDebut} onChange={(e)=> setDateDebut(e.target.value)} required />
        </div>
        <div>
          <label>Date Fin: </label>
          <input type="date" value={dateFin} onChange={(e)=> setDateFin(e.target.value)} required />
        </div>
        <button type="submit">Set Period</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default SetPeriod;
