// src/App.js
import React from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import UploadElectors from './components/UploadElectors';
import ValidateElectors from './components/ValidateElectors';
import AddCandidate from './components/AddCandidate';
import SetPeriod from './components/SetPeriod';

function PrivateRoute({ component: Component, ...rest }) {
  return (
    <Route 
      {...rest} 
      render={props => 
        localStorage.getItem('token') ? (
          <Component {...props} />
        ) : (
          <Redirect to="/login" />
        )
      } 
    />
  );
}

function App() {
  return (
    <div>
      <Switch>
        <Route exact path="/login" component={Login} />
        <PrivateRoute exact path="/" component={Dashboard} />
        <PrivateRoute exact path="/upload-electors" component={UploadElectors} />
        <PrivateRoute exact path="/validate-electors" component={ValidateElectors} />
        <PrivateRoute exact path="/add-candidate" component={AddCandidate} />
        <PrivateRoute exact path="/set-period" component={SetPeriod} />
        <Redirect to="/" />
      </Switch>
    </div>
  );
}

export default App;
