// src/components/CandidateDetails.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';

function CandidateDetails() {
  const { id } = useParams();
  const [candidate, setCandidate] = useState(null);
  const [newCode, setNewCode] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchCandidate = async () => {
      try {
        const token = localStorage.getItem('token');
        // On suppose que l'API accepte un paramètre "id" pour récupérer les détails d'un candidat.
        const response = await axios.get(`http://localhost:8000/candidats/get_candidate.php?id=${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        setCandidate(response.data);
      } catch (error) {
        console.error(error);
        setMessage('Erreur lors de la récupération des détails du candidat.');
      }
    };
    fetchCandidate();
  }, [id]);

  const handleGenerateNewCode = async () => {
    try {
      const token = localStorage.getItem('token');
      // On suppose qu'un endpoint "generate_code.php" existe pour générer un nouveau code d'authentification
      const response = await axios.post(
        'http://localhost:8000/candidats/generate_code.php',
        { candidateId: id },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );
      setNewCode(response.data.newCode);
      setMessage('Nouveau code généré avec succès.');
    } catch (error) {
      console.error(error);
      setMessage("Erreur lors de la génération du nouveau code.");
    }
  };

  if (!candidate) {
    return (
      <div>
        <Navbar />
        <p>Chargement des détails du candidat...</p>
      </div>
    );
  }

  return (
    <div>
      <Navbar />
      <h2>Détails du candidat</h2>
      <p><strong>Nom :</strong> {candidate.nom}</p>
      <p><strong>Prénom :</strong> {candidate.prenom}</p>
      <p><strong>Date de naissance :</strong> {candidate.date_naissance}</p>
      <p><strong>Bureau de vote :</strong> {candidate.bureau_vote}</p>
      <p><strong>Email :</strong> {candidate.email}</p>
      <p><strong>Téléphone :</strong> {candidate.telephone}</p>
      <p><strong>Parti Politique :</strong> {candidate.parti_politique}</p>
      <p><strong>Slogan :</strong> {candidate.slogan}</p>
      <p>
        <strong>Photo :</strong>{' '}
        {candidate.photo ? <img src={candidate.photo} alt={candidate.nom} width="150" /> : 'Aucune photo'}
      </p>
      <p>
        <strong>Couleurs :</strong> {candidate.couleur1}, {candidate.couleur2}, {candidate.couleur3}
      </p>
      <p>
        <strong>URL :</strong>{' '}
        {candidate.url ? (
          <a href={candidate.url} target="_blank" rel="noreferrer">
            {candidate.url}
          </a>
        ) : (
          'Aucune URL'
        )}
      </p>
      {message && <p>{message}</p>}
      {newCode && <p><strong>Nouveau code :</strong> {newCode}</p>}
      <button onClick={handleGenerateNewCode}>Générer un nouveau code d'authentification</button>
    </div>
  );
}

export default CandidateDetails;
